import React, { useState, useEffect } from 'react';
import LoginForm from './components/auth/LoginForm';
import RegisterForm from './components/auth/RegisterForm';
import Dashboard from './components/dashboard/Dashboard';
import { Container } from '@mui/material';
import { User } from './types/User';

const App: React.FC = () => {
  const [view, setView] = useState<'login' | 'register' | 'dashboard'>('login');
  const [user, setUser] = useState<User | null>(null);

  useEffect(() => {
    const loggedUsername = localStorage.getItem('loggedInUser');
    if (loggedUsername) {
      const userData = localStorage.getItem(loggedUsername);
      if (userData) {
        setUser(JSON.parse(userData));
        setView('dashboard');
      }
    }
  }, []);

  const handleLogin = (username: string) => {
    const userData = localStorage.getItem(username);
    if (userData) {
      localStorage.setItem('loggedInUser', username);
      setUser(JSON.parse(userData));
      setView('dashboard');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('loggedInUser');
    setUser(null);
    setView('login');
  };

  return (
    <Container>
      {view === 'login' && <LoginForm onLogin={handleLogin} onSwitch={() => setView('register')} />}
      {view === 'register' && <RegisterForm onSwitch={() => setView('login')} />}
      {view === 'dashboard' && user && <Dashboard onLogout={handleLogout} username={user.username} />}
    </Container>
  );
};

export default App;
