import React, { useState } from 'react';
import { Avatar } from '@mui/material';
import bcrypt from 'bcryptjs';
import styles from './login-register.module.scss';

interface RegisterFormProps {
  onSwitch: () => void;
}

const RegisterForm: React.FC<RegisterFormProps> = ({ onSwitch }) => {
  const [form, setForm] = useState({ username: '', password: '', confirm: '' });
  const [avatar, setAvatar] = useState<string | null>(null);
  const [errors, setErrors] = useState<{ [key: string]: string }>({});

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setForm({ ...form, [e.target.name]: e.target.value });
    setErrors((prev) => ({ ...prev, [e.target.name]: '' }));
  };

  const handleAvatar = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setAvatar(reader.result as string);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const newErrors: { [key: string]: string } = {};

    if (!form.username) newErrors.username = 'Попълни потребителско име';
    if (!form.password) newErrors.password = 'Попълни парола';
    else if (form.password.length < 6) newErrors.password = 'Паролата трябва да е поне 6 символа';
    if (!form.confirm) newErrors.confirm = 'Потвърди паролата';
    else if (form.password !== form.confirm) newErrors.confirm = 'Паролите не съвпадат';

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    if (localStorage.getItem(form.username)) {
      alert('Потребителят вече съществува!');
      return;
    }

    const hashedPassword = await bcrypt.hash(form.password, 10);

    const userData = {
      username: form.username,
      password: hashedPassword,
      avatar,
    };

    localStorage.setItem(form.username, JSON.stringify(userData));
    alert('Регистрацията е успешна!');
    onSwitch();
  };

  return (
    <div className={styles.mainSignupLogin}>
      <div className={styles.headerSignupLogin}>
        <h1>Регистрация</h1>
      </div>

      <form onSubmit={handleSubmit} className={styles.loginSignupForm}>
        <div className={styles.avatarUpload}>
          <Avatar
            src={avatar || undefined}
            sx={{ width: 100, height: 100, border: '2px solid grey', marginBottom: '10px' }}
          />
          <label htmlFor="upload" style={{ cursor: 'pointer' }}>
            <input
              accept="image/*"
              id="upload"
              type="file"
              hidden
              onChange={handleAvatar}
            />
            <span className={styles.iconButton}>Добави икона</span>
          </label>
        </div>

        <label>Потребителско име</label>
        <input
          className={`${styles.inputSignupLogin} ${errors.username ? styles.inputError : ''}`}
          type="text"
          name="username"
          placeholder="Въведи потребителско име"
          value={form.username}
          onChange={handleChange}
          required
        />
        {errors.username && <span className={styles.errorText}>{errors.username}</span>}

        <label>Парола</label>
        <input
          className={`${styles.inputSignupLogin} ${errors.password ? styles.inputError : ''}`}
          type="password"
          name="password"
          placeholder="Трябва да съдържа минимум 6 символа"
          value={form.password}
          onChange={handleChange}
          required
        />
        {errors.password && <span className={styles.errorText}>{errors.password}</span>}

        <label>Повтори паролата</label>
        <input
          className={`${styles.inputSignupLogin} ${errors.confirm ? styles.inputError : ''}`}
          type="password"
          name="confirm"
          placeholder="Въведи отново паролата"
          value={form.confirm}
          onChange={handleChange}
          required
        />
        {errors.confirm && <span className={styles.errorText}>{errors.confirm}</span>}

        <button type="submit" className={styles.submitButton}>Регистрация</button>

        <p className={styles.redirectInformation}>
          Вече имаш акаунт?{' '}
          <span className={styles.redirectLink} onClick={onSwitch}>Влез</span>
        </p>
      </form>
    </div>
  );
};

export default RegisterForm;
