import React, { useState } from 'react';
import { Container } from '@mui/material';
import bcrypt from 'bcryptjs';
import styles from './login-register.module.scss';

interface LoginFormProps {
  onLogin: (username: string) => void;
  onSwitch: () => void;
}

const LoginForm: React.FC<LoginFormProps> = ({ onLogin, onSwitch }) => {
  const [form, setForm] = useState({ username: '', password: '' });
  const [errors, setErrors] = useState<{ [key: string]: string }>({});

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setForm({ ...form, [e.target.name]: e.target.value });
    setErrors((prev) => ({ ...prev, [e.target.name]: '' }));
  };

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const newErrors: { [key: string]: string } = {};

    if (!form.username) newErrors.username = 'Попълни потребителско име';
    if (!form.password) newErrors.password = 'Попълни парола';

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    const data = localStorage.getItem(form.username);
    if (!data) {
      setErrors({ username: 'Няма такъв потребител.' });
      return;
    }

    const user = JSON.parse(data);
    const match = await bcrypt.compare(form.password, user.password);
    if (!match) {
      setErrors({ password: 'Грешна парола!' });
      return;
    }

    onLogin(form.username);
  };

  return (
    <Container className={styles.mainSignupLogin}>
      <div className={styles.headerSignupLogin}>
        <h1>Вход</h1>
      </div>
      <form onSubmit={handleSubmit} className={styles.loginSignupForm}>
        <input
          className={`${styles.inputSignupLogin} ${errors.username ? styles.inputError : ''}`}
          type="text"
          name="username"
          placeholder="Потребителско име"
          value={form.username}
          onChange={handleChange}
          required
        />
        {errors.username && <span className={styles.errorText}>{errors.username}</span>}

        <input
          className={`${styles.inputSignupLogin} ${errors.password ? styles.inputError : ''}`}
          type="password"
          name="password"
          placeholder="Парола"
          value={form.password}
          onChange={handleChange}
          required
        />
        {errors.password && <span className={styles.errorText}>{errors.password}</span>}

        <button type="submit" className={styles.submitButton}>Вход</button>
        <p className={styles.redirectInformation}>
          Нямаш акаунт? <span className={styles.redirectLink} onClick={onSwitch}>Регистрирай се</span>
        </p>
      </form>
    </Container>
  );
};

export default LoginForm;
