import React, { createContext, useContext, useState, useEffect } from 'react';
import jwtDecodeLib from 'jwt-decode';

// üëá –ü—Ä–∏–Ω—É–¥–∏—Ç–µ–ª–Ω–æ –∫–∞—Å—Ç–≤–∞–Ω–µ –∫—ä–º —Ñ—É–Ω–∫—Ü–∏—è
const jwt_decode = jwtDecodeLib as unknown as <T>(token: string) => T;

interface DecodedToken {
  username: string;
  avatar?: string;
  exp: number;
}

interface AuthContextType {
  token: string | null;
  user: DecodedToken | null;
  login: (token: string) => void;
  logout: () => void;
}

const AuthContext = createContext<AuthContextType>({
  token: null,
  user: null,
  login: () => {},
  logout: () => {},
});

export const AuthProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [token, setToken] = useState<string | null>(localStorage.getItem('token'));
  const [user, setUser] = useState<DecodedToken | null>(() => {
    const stored = localStorage.getItem('token');
    return stored ? jwt_decode<DecodedToken>(stored) : null;
  });

  useEffect(() => {
    if (token) {
      setUser(jwt_decode<DecodedToken>(token));
      localStorage.setItem('token', token);
    } else {
      setUser(null);
      localStorage.removeItem('token');
    }
  }, [token]);

  const login = (newToken: string) => {
    setToken(newToken);
  };

  const logout = () => {
    setToken(null);
  };

  return (
    <AuthContext.Provider value={{ token, user, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
