import React, { useState } from 'react';
import { useAuth } from '../../context/AuthContext';
import axiosInstance from '../api/axiosConfig';
import { useNavigate } from 'react-router-dom';

const RegisterForm = () => {
  const [form, setForm] = useState({ username: '', password: '', email: '' });
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setForm(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const response = await axiosInstance.post('/auth/register', form);
      const token = response.data;
      login(token); // автоматичен вход чрез context
      navigate('/'); // пренасочване към начална или защитена страница
    } catch (error) {
      console.error('Registration failed:', error);
      alert('Потребителското име вече съществува или има друг проблем.');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        name="username"
        value={form.username}
        onChange={handleChange}
        placeholder="Username"
        required
      />
      <input
        name="email"
        value={form.email}
        onChange={handleChange}
        placeholder="Email"
        type="email"
        required
      />
      <input
        name="password"
        value={form.password}
        onChange={handleChange}
        placeholder="Password"
        type="password"
        required
      />
      <button type="submit">Register</button>
    </form>
  );
};

export default RegisterForm;
