import React, { useState } from 'react';
import { Avatar } from '@mui/material';
import styles from './dashboard.module.scss';
import { User } from '../../types/User';

interface DashboardProps {
  username: string;
  onLogout: () => void;
}

const Dashboard: React.FC<DashboardProps> = ({ onLogout, username }) => {
  const [user, setUser] = useState<User | null>(() => {
    const data = localStorage.getItem(username);
    return data ? JSON.parse(data) : null;
  });

  const notes = [
    { title: 'Пазар', content: 'Хляб, мляко, яйца' },
    { title: 'Домашно', content: 'Завърши React проект' },
    { title: 'План', content: 'Понеделник: React, Вторник: Node.js' }
  ];

  const handleAvatarChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        const updatedUser: User = { ...user!, avatar: reader.result as string };
        localStorage.setItem(username, JSON.stringify(updatedUser));
        setUser(updatedUser);
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <>
      <header className={styles.dashboardHeader}>
        <Avatar
          src={user?.avatar || undefined}
          alt="User avatar"
          sx={{ width: 100, height: 100, margin: '0 auto 10px auto', border: '3px solid black' }}
        />
        <label htmlFor="change-avatar" style={{ marginBottom: '10px' }}>
          <input
            type="file"
            id="change-avatar"
            accept="image/*"
            hidden
            onChange={handleAvatarChange}
          />
          <span className={styles.iconButton}>
            {user?.avatar ? 'Смени икона' : 'Добави икона'}
          </span>
        </label>

        <h1>Notes in the cloud</h1>
        <h2>Dashboard</h2>
      </header>

      <main className={styles.mainSection}>
        <div className={styles.notesContainer}>
          {notes.map((n, i) => (
            <div key={i} className={styles.noteCard}>
              <h3>{n.title}</h3>
              <p>{n.content}</p>
            </div>
          ))}
        </div>
        <button onClick={onLogout} className={styles.submitButton}>Изход</button>
      </main>
    </>
  );
};

export default Dashboard;
